import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './Memories.css';

interface Memory {
  id: number;
  title: string;
  description: string;
  date: string;
  imageUrl: string;
  story: string;
  location: string;
}

const Memories: React.FC = () => {
  const [memories, setMemories] = useState<Memory[]>([
    {
      id: 1,
      title: "First Call with Edeline",
      description: "The moment I first saw your beautiful smile through the screen, my pretty flower. My heart skipped a beat, and I knew my beautiful chaos was special.",
      date: "",
      imageUrl: "https://images.unsplash.com/photo-1516589178581-6cd7833ae3b2?w=400&h=300&fit=crop",
      story: "I'll never forget the butterflies I felt when I first heard your voice on that callüò≠üíï/n. You voice became my favourite melody. That was the moment I knew you were someone special, someone who would change my life forever.",
      location: "Voice Call"
    },
    {
      id: 2,
      title: "Late Night Conversations with My Pretty Flower",
      description: "Talking until the early hours with youuu, sharing dreams, fears, and everything in between. These moments made us closer.",
      date: "",
      imageUrl: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=300&fit=crop",
      story: "Those late-night conversations where we talked about everything and nothing. Time seemed to stop when we were together, even virtually. We shared our deepest thoughts, our wildest dreams, and our biggest fears. Those moments made me realize how perfectly we connect, how easily we understand each other.",
      location: "Our Hearts"
    },
    {
      id: 3,
      title: "Our First 'Good Morning', Eddy",
      description: "That sweet message that started our daily tradition. Every morning feels brighter knowing I'll hear from my beautiful chaos.",
      date: "January 1, 2025",
      imageUrl: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=300&fit=crop",
      story: "Waking up to your good morning messages became my favorite part of every day. Your words were like sunshine breaking through my window, filling my heart with warmth and love. No matter how far apart we are, you're always the first thing on my mind when I wake up.",
      location: "Every Morning"
    },
    {
      id: 4,
      title: "Planning Our Future",
      description: "Dreaming about the day we'll be together",
      date: "January 5, 2025",
      imageUrl: "https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=400&h=300&fit=crop",
      story: "The hours we spend planning our future together - where we'll live, the places we'll visit, the life we'll build. Every plan we make feels like a promise, a step closer to our forever. These conversations fill me with so much hope and excitement for what's to come.",
      location: "Our Dreams"
    },
    {
      id: 6,
      title: "Virtual Dates",
      description: "Creative ways to date across the distance",
      date: "January 10, 2025",
      imageUrl: "https://images.unsplash.com/photo-1511632765486-a01980e01a18?w=400&h=300&fit=crop",
      story: "Our virtual dinner dates, online game nights, and synchronized walks while video calling. You've shown me that love isn't limited by distance - it's about creativity, effort, and the desire to share moments together. Every virtual date feels like a real adventure with you.",
      location: "Everywhere & Nowhere"
    }
  ]);

  const [selectedMemory, setSelectedMemory] = useState<Memory | null>(null);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0, rotateY: -15 },
    visible: {
      y: 0,
      opacity: 1,
      rotateY: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <motion.div 
      className="memories-page"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <div className="container">
        <motion.div className="page-header" variants={itemVariants}>
          <h1 className="romantic-title">Our Memories, Eddy</h1>
          <p className="romantic-subtitle">
            Precious moments with my pretty flower, captured in time
          </p>
        </motion.div>

        <motion.div className="memories-grid" variants={containerVariants}>
          {memories.map((memory) => (
            <motion.div
              key={memory.id}
              className="memory-card polaroid"
              variants={itemVariants}
              layoutId={`memory-${memory.id}`}
              onClick={() => setSelectedMemory(memory)}
              whileHover={{ 
                scale: 1.05, 
                rotateY: 5,
                rotateX: 5,
                boxShadow: "0 20px 40px rgba(155, 93, 229, 0.3)"
              }}
              whileTap={{ scale: 0.95 }}
            >
              <div className="polaroid-image">
                <img src={memory.imageUrl} alt={memory.title} />
                <div className="image-overlay">
                  <span className="view-story">üìñ View Story</span>
                </div>
              </div>
              <div className="polaroid-caption">
                <h3 className="memory-title">{memory.title}</h3>
                <p className="memory-description">{memory.description}</p>
                <div className="memory-meta">
                  <span className="memory-date">üìÖ {memory.date}</span>
                  <span className="memory-location">üìç {memory.location}</span>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        <AnimatePresence>
          {selectedMemory && (
            <motion.div
              className="memory-modal"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setSelectedMemory(null)}
            >
              <motion.div
                className="memory-story"
                layoutId={`memory-${selectedMemory.id}`}
                onClick={(e) => e.stopPropagation()}
                initial={{ scale: 0.8, y: 50 }}
                animate={{ scale: 1, y: 0 }}
                exit={{ scale: 0.8, y: 50 }}
                transition={{ duration: 0.3 }}
              >
                <button 
                  className="close-btn"
                  onClick={() => setSelectedMemory(null)}
                >
                  ‚úï
                </button>

                <div className="story-image">
                  <img src={selectedMemory.imageUrl} alt={selectedMemory.title} />
                </div>

                <div className="story-content">
                  <div className="story-header">
                    <h2 className="story-title">{selectedMemory.title}</h2>
                    <div className="story-meta">
                      <span className="story-date">üìÖ {selectedMemory.date}</span>
                      <span className="story-location">üìç {selectedMemory.location}</span>
                    </div>
                  </div>

                  <p className="story-text">{selectedMemory.story}</p>

                  <div className="story-footer">
                    <span className="heart-decoration">üíï</span>
                    <p className="story-signature">Forever in my heart</p>
                    <span className="heart-decoration">üíï</span>
                  </div>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
};

export default Memories;
