import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './LoveLetters.css';

interface Letter {
  id: number;
  title: string;
  content: string;
  date: string;
  mood: string;
}

const HerLetters: React.FC = () => {
  const [letters, setLetters] = useState<Letter[]>([
    {
      id: 1,
      title: "Happy Birthday Princess üòπüíï",
      content: "Happy birthday to my husband, the love of my life, my joy giverrr, my princessss, my batman, my personal AI, my boyfrienddd ü´∂, my happiness ü•π‚ù§Ô∏è\n\nI wish you long life and prosperity ‚ú®\nMore deals to youuu ‚ú®\nMay your secret prayers be answered üíï\nI pray that the Lord will continue to bless, guide and strengthen you in every area of your life ü´∂üèΩ\nKeep being the amazing person that you are ü•πü´∂üèΩ\nI LOVE YOU SOOO MUCHHHHH ü•∫‚ù§Ô∏è\n\nAs you celebrate this special dayyy I just want you to know that I am lucky to have you ‚Äî you make life better ü•π\nI can't even start to say how much better my life is with you in it. You've shown me what true love is ü•∫ and I'll forever be grateful for having you in my life.\nThank you for showing me that true love exists üò≠‚ù§Ô∏è‚ù§Ô∏è\nThank you for bringing so much happiness into my life ü•∫üò≠\n\nEveryday with you is a reminder of how beautiful life can be ü•π\nI promise to always love you, support you and stand by you no matter the circumstances ü´†üíï\nI love you, words can't express how much I actually do ü§ß\n\nHere's to many more beautiful memories together ‚ú®ü•Ç\n\nI hope you have a wonderful and amazing day ü§ß‚ù§Ô∏è‚ù§Ô∏è\nI LOVE YOUU SOOOO MUCHHHHHHH üòô‚ù§Ô∏è‚ù§Ô∏è",
      date: "January 26, 2025",
      mood: "üíï"
    },    
    {
      id: 2,
      title: "Random Love message you gave me when i asked you to make me cryü§ßüòπ‚ù§Ô∏è",
      content: "I know I can be a lot sometimes - my mood swings, my overthinking, my need for constant reassurance. But you handle all of it with such patience and love. You never make me feel like I'm too much.\n\nI love how you call me your beautiful chaos because that's exactly what I am, and somehow you love me for it. Thank you for choosing me every day, even on the days when I'm not easy to love.",
      date: "August 10, 2025",
      mood: "üåπ"
    }
  ]);

  const [selectedLetter, setSelectedLetter] = useState<Letter | null>(null);
  const [showNewLetterForm, setShowNewLetterForm] = useState(false);
  const [newLetter, setNewLetter] = useState({ title: '', content: '', mood: 'üíï' });

  const moodOptions = ['üíï', 'üíñ', 'üíó', 'üíù', 'üíò', '‚ù§Ô∏è', 'ü§ç', 'üíú', '‚ú®', 'üåü'];

  const handleAddLetter = () => {
    if (newLetter.title.trim() && newLetter.content.trim()) {
      const letter: Letter = {
        id: letters.length + 1,
        title: newLetter.title,
        content: newLetter.content,
        date: new Date().toLocaleDateString('en-US', { 
          year: 'numeric', 
          month: 'long', 
          day: 'numeric' 
        }),
        mood: newLetter.mood
      };
      setLetters([...letters, letter]);
      setNewLetter({ title: '', content: '', mood: 'üíï' });
      setShowNewLetterForm(false);
    }
  };

  return (
    <motion.div 
      className="love-letters-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8 }}
    >
      <div className="love-letters-content">
        <motion.header 
          className="page-header"
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
        >
          <h1>Letters from youuüíå</h1>
          <p>Beautiful words from my pretty flower to me</p>
        </motion.header>

        <motion.div 
          className="letters-grid"
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.4, duration: 0.6 }}
        >
          {letters.map((letter, index) => (
            <motion.div
              key={letter.id}
              className="letter-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 * index, duration: 0.5 }}
              whileHover={{ scale: 1.02, rotateY: 2 }}
              onClick={() => setSelectedLetter(letter)}
            >
              <div className="letter-header">
                <h3>{letter.title}</h3>
                <span className="letter-mood">{letter.mood}</span>
              </div>
              <p className="letter-preview">
                {letter.content.substring(0, 100)}...
              </p>
              <div className="letter-date">{letter.date}</div>
            </motion.div>
          ))}

          <motion.div
            className="letter-card add-letter-card"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => setShowNewLetterForm(true)}
          >
            <div className="add-letter-content">
              <span className="add-icon">‚úçÔ∏è</span>
              <h3>Add New Letter</h3>
              <p>Capture another beautiful moment</p>
            </div>
          </motion.div>
        </motion.div>

        <AnimatePresence>
          {selectedLetter && (
            <motion.div
              className="letter-modal-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setSelectedLetter(null)}
            >
              <motion.div
                className="letter-modal"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.8, opacity: 0 }}
                onClick={(e) => e.stopPropagation()}
              >
                <div className="modal-header">
                  <h2>{selectedLetter.title}</h2>
                  <span className="modal-mood">{selectedLetter.mood}</span>
                  <button 
                    className="close-button"
                    onClick={() => setSelectedLetter(null)}
                  >
                    √ó
                  </button>
                </div>
                <div className="modal-content">
                  <p>{selectedLetter.content}</p>
                </div>
                <div className="modal-footer">
                  <span className="modal-date">{selectedLetter.date}</span>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        <AnimatePresence>
          {showNewLetterForm && (
            <motion.div
              className="letter-modal-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setShowNewLetterForm(false)}
            >
              <motion.div
                className="letter-modal new-letter-modal"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.8, opacity: 0 }}
                onClick={(e) => e.stopPropagation()}
              >
                <div className="modal-header">
                  <h2>Write a New Letter</h2>
                  <button 
                    className="close-button"
                    onClick={() => setShowNewLetterForm(false)}
                  >
                    √ó
                  </button>
                </div>
                <div className="modal-content">
                  <div className="form-group">
                    <label>Title:</label>
                    <input
                      type="text"
                      value={newLetter.title}
                      onChange={(e) => setNewLetter({...newLetter, title: e.target.value})}
                      placeholder="Give your letter a title..."
                    />
                  </div>
                  <div className="form-group">
                    <label>Mood:</label>
                    <div className="mood-selector">
                      {moodOptions.map((mood) => (
                        <button
                          key={mood}
                          className={`mood-option ${newLetter.mood === mood ? 'selected' : ''}`}
                          onClick={() => setNewLetter({...newLetter, mood})}
                        >
                          {mood}
                        </button>
                      ))}
                    </div>
                  </div>
                  <div className="form-group">
                    <label>Letter:</label>
                    <textarea
                      value={newLetter.content}
                      onChange={(e) => setNewLetter({...newLetter, content: e.target.value})}
                      placeholder="Write your heart out..."
                      rows={8}
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button 
                    className="cancel-button"
                    onClick={() => setShowNewLetterForm(false)}
                  >
                    Cancel
                  </button>
                  <button 
                    className="save-button"
                    onClick={handleAddLetter}
                  >
                    Save Letter
                  </button>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
};

export default HerLetters;
